mapper.select:
SELECT id, user_id, category_id, title, subtitle, goal_amount, current_amount, status, start_date, end_date
FROM project
WHERE id = #{projectId}

SELECT id, user_id, category_id, title, subtitle, goal_amount, current_amount, status, start_date, end_date
FROM project
WHERE user_id = #{userId}

mapper.update:
UPDATE project
user_id = #{userId}, category_id = #{categoryId}, title = #{title}, subtitle = #{subtitle}, goal_amount= #{goalAmount}, current_amount = #{currentAmount}, status = #{status}
WHERE id = #{id}
