mapper.select:
SELECT id, member_id, title, description, target_amount, status
FROM proposal
WHERE id = #{proposalId}

SELECT id, member_id, title, description, target_amount, status
FROM proposal
WHERE member_id = #{memberId}

mapper.update:
UPDATE proposal SET
	title = #{title},
	description = #{description},
	target_amount = #{targetAmount},
	status = #{status}
WHERE id = #{id}
